drop table credit_type_user;
create table credit_type_user as 
select u.mobile,
t.loan_card_pan,
c.capital_limit, 
t.out_range,
case when substr(c.product_no,1,2) in ('PA','PJ') then 'tnh' 
     else 'yfq' end as        
t.PAY_TIME,
c.start_date,
c.due_date,
c.contract_no 
 from r_creditloan.s_c_loan_apply  a 
inner join r_creditloan.s_c_apply_user u on u.id = a.id
inner join creditloan.s_c_loan_contract c on c.apply_id = a.id 
inner join r_creditloan.s_c_receipt t on t.contract_id = c.id 
where a.year='2017' and a.month='06' and a.day='15'
and u.year='2017' and u.month='06' and u.day='15'
and c.year='2017' and c.month='06' and c.day='15'
and t.year='2017' and t.month='06' and t.day='15'
and c.status <> 'F'
and substr(c.product_no,1,2) in ('PA','PJ','PC','PK')
and t.pay_time <> 'null';


